mapper.select:
select		*
from		(
				select		ROWNUM as RNUM, T.*
				from		titles T
				
					
						((#{pageNum} - 1) * 5) + 1 <= ROWNUM
					
					
						and request_status = #{requestStatusFilter}
					
					
						and request_type = #{requestTypeFilter}
					
					
						and sender_id<>#{userId}
					
				
			)

	
		(#{pageNum} * 5) + 1 > RNUM

select		*
from		(
				select		ROWNUM as RNUM, T.*
				from		titles T
				
					
						((#{pageNum} - 1) * 5) + 1 <= ROWNUM
					
					
						and request_status = #{requestStatusFilter}
					
					
						and request_type = #{requestTypeFilter}
					
					
						and sender_id<>#{userId}
					
				
				order by	delivery_price DESC
			)

	
		(#{pageNum} * 5) + 1 > RNUM

select		*
from		(
				select		ROWNUM as RNUM, T.*
				from		titles T
				
					
						((#{pageNum} - 1) * 5) + 1 <= ROWNUM
					
					
						and request_status = #{requestStatusFilter}
					
					
						and request_type = #{requestTypeFilter}
					
					
						and sender_id<>#{userId}
					
				
				order by	sender_avg DESC
			)

	
		(#{pageNum} * 5) + 1 > RNUM

select		*
from		(
				select		ROWNUM as RNUM, T.*
				from		titles T
				
					
						((#{pageNum} - 1) * 5) + 1 <= ROWNUM
					
					
						and request_status = #{requestStatusFilter}
					
					
						and request_type = #{requestTypeFilter}
					
					
						and sender_id<>#{userId}
					
				
				
					order by	expire_date DESC
				
				
					order by	expire_date ASC
				
			)

	
		(#{pageNum} * 5) + 1 > RNUM

select		*
from		(
				select		ROWNUM as RNUM, T.*
				from		titles T
				
					
						((#{pageNum} - 1) * 5) + 1 <= ROWNUM
					
					
						and (pickup_place=#{startPos} or pickup_place is null)
					
					
						and arrival_place=#{arrivalPos}
					
					
						and ((pick_up_min_time<=#{startTime} and pick_up_max_time>=#{startTime})
							or (pick_up_min_time is null and pick_up_max_time is null))
					
					
						and (arrival_min_time<=#{arrivalTime} and arrival_max_time>=#{arrivalTime})
					
					
						and request_status = #{requestStatusFilter}
					
					
						and request_type = #{requestTypeFilter}
					
					
						and sender_id<>#{userId}
					
				
			)

	
		(#{pageNum} * 5) + 1 > RNUM
