		)
		
		values(#{senderId}, #{requestType}, #{cargoName}, #{deliveryPrice}
		
			
				, #{cargoPicture}
			
			
, #{cargoDesc},#{senderPhone}, #{pickupMinTime}, #{pickupMaxTime}, #{pickupPlace}, #{pickupPlaceDesc}, #{receiverName}, #{receiverPhone}, #{arrivalMinTime}, #{arrivalMaxTime}, #{arrivalPlace}, #{arrivalPlaceDesc}, #{absenceMessage}, #{expireDate}
		)
